mapper.select:

	select * from (select rownum rnum, product_num, product_l, product_m, product_name, product_price,
	product_content, product_sub, product_img, product_stock from (select * from product order by product_num desc))
   	where rnum >= ((#{page}-1) * 10+1)  and rnum <= (#{page} * 10) and product_l = #{product_l}

select * from (select a.*,rowNum rn from ( select P.product_num as product_num, P.product_l as product_l, P.produt_m as product_m, P.product_name as product_name, P.product_price as product_price, P.product_content as product_content, P.product_sub as product_sub, P.product_img as product_img, P.product_stock as product_stock from product P left outer join product_img PI on P.product_num = PI.product_num and PI.p_img_order=1
product_stock=#{stock} and ${search} like '%'||#{keyword}||'%'
product_stock='1' and ${search} like '%'||#{keyword}||'%'
order by
) a ) where rn between #{startRow} and #{endRow}
